        const pageWidth = this.pdf.internal.pageSize.getWidth();
        const titular = data.result.identificacionTitular[0];

        // Logo
        this.pdf.addImage(this.LOGO_BASE64, 'JPEG', this.margin, 10, 50, 20);

        // Datos de la tabla
